import React, { Component } from "react";
import axios from "axios";
import ModifContact from "./ModifContact";
import TextField from "@material-ui/core/TextField";

class allContact extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isCliked: false,
      data: [],
      loading: true,
      value: ""
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  // state = {
  //   loading: true,
  //   data: [],
  //   isCliked: false;

  // };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    axios
      .get("http://localhost:3006/contacts/all")
      .then(response => {
        console.log(response.data);
        this.setState({
          data: response.data,

          loading: false
        });
      })
      .catch(err => console.log(err));
  };

  handleClick(value) {
    console.log(value);
    this.setState({
      isCliked: !this.state.isCliked
    });
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleClickBtn = id => {
    console.log("ok");
    axios
      .delete("http://localhost:3006/contacts/del/" + id)
      .then(res => this.fetchData())
      .catch(err => console.log("error"));
  };

  sortNom = letter => {
    console.log(letter);
    axios
      .get(`http://localhost:3006/contacts/search-letter/${letter}`)
      .then(({ data }) => {
        this.setState({
          data
        });
      })
      .catch(err => console.log("error"));
  };

  render() {
    if (this.state.loading === true) {
      return <h1>Loading...</h1>;
    } else {
      return (
        <div>
          <h1 className="text-center">Project</h1>
          <br />
          <br />
          <div className="search"></div>
          <p className="alphabet-sort">
            <span onClick={() => this.sortNom("a")}>A</span>
            <span onClick={() => this.sortNom("b")}>B</span>
            <span onClick={() => this.sortNom("c")}>C</span>
            <span onClick={() => this.sortNom("d")}>D</span>
            EFGHIJKLMNOPQRSTUVWXYZ
          </p>
          {this.state.data.map((value, index) => {
            return (
              <div className="container" key={index}>
                <ModifContact
                  nom={value.nom}
                  prenom={value.prenom}
                  // image={value.image}
                  email={value.email}
                  numero={value.numero}
                  fonction={value.fonction}
                  date={value.date}
                  id={value._id}
                  handleClickBtn={this.handleClickBtn}
                />
                <br />
                <br />
              </div>
            );
          })}
        </div>
      );
    }
  }
}

export default allContact;
